import { useState } from "react";
import { useForm, Controller } from "react-hook-form";
import { Select, TextField, Button, Container } from "@material-ui/core"; // Importe os componentes adequados

const CreateLessonForm = () => {
  const [selectedFormador, setSelectedFormador] = useState(null);
  const [selectedModule, setSelectedModule] = useState(null);
  const [selectedCourse, setSelectedCourse] = useState(null);

  const {
    control,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = (data) => {
    // Enviar dados para a API ou realizar ações necessárias com os dados do formulário
    console.log(data);
  };

  return (
    <Container>
      <form onSubmit={handleSubmit(onSubmit)}>
        <div>
          <label>Título da aula:</label>
          <Controller
            name="lessonTitle"
            control={control}
            rules={{ required: "Este campo é obrigatório" }}
            render={({ field }) => (
              <TextField {...field} placeholder="Digite o título da aula" />
            )}
          />
          {errors.lessonTitle && (
            <span>{errors.lessonTitle.message}</span>
          )}
        </div>
        <div>
          <label>Data de início:</label>
          <Controller
            name="lessonStart"
            control={control}
            rules={{ required: "Este campo é obrigatório" }}
            render={({ field }) => (
              <TextField {...field} type="date" />
            )}
          />
          {errors.lessonStart && (
            <span>{errors.lessonStart.message}</span>
          )}
        </div>
        <div>
          <label>Data de término:</label>
          <Controller
            name="lessonEnd"
            control={control}
            rules={{ required: "Este campo é obrigatório" }}
            render={({ field }) => (
              <TextField {...field} type="date" />
            )}
          />
          {errors.lessonEnd && (
            <span>{errors.lessonEnd.message}</span>
          )}
        </div>
        <div>
          <label>Formador:</label>
          <Select
            value={selectedFormador}
            onChange={(e) => setSelectedFormador(e.target.value)}
          >
            {/* Opções do select para formadores */}
          </Select>
        </div>
        <div>
          <label>Módulo:</label>
          <Select
            value={selectedModule}
            onChange={(e) => setSelectedModule(e.target.value)}
          >
            {/* Opções do select para módulos */}
          </Select>
        </div>
        <div>
          <label>Curso:</label>
          <Select
            value={selectedCourse}
            onChange={(e) => setSelectedCourse(e.target.value)}
          >
            {/* Opções do select para cursos */}
          </Select>
        </div>
        <div>
          <Button type="submit" variant="contained" color="primary">
            Criar Aula
          </Button>
        </div>
      </form>
    </Container>
  );
};

export default CreateLessonForm;
