import styled from "styled-components";
import React, { useState, useRef, useEffect } from "react";
import ReactAsync from "react-select/async";
import "./CalendarStyles.css";
import {

	ContainerHeader,
	SmallContainer,
	Header,
	TableIconContainer,
	TableRow,
	Search,
	Edit,
	Add,
	EditIcon,
	HeaderDivider,
	TableField,
} from "../../../styles/AdminStyles";
import { api } from "../../../api/api";
import { Notify } from "notiflix";

export default function Index() {

  const [teachers, setTeachers] = useState([]);
  const [selectedTeacher, setSelectedTeacher] = useState(null);
  const [key, setKey] = useState(0);
  const [teacherParam, setTeacherParam] = useState("all")


  useEffect(() => {
    setKey(prevKey => prevKey + 1); // Incrementa a chave para forçar a renderização
  }, [selectedTeacher]);

  useEffect(() => {

    api.get('/teachers')
      .then(response => {
        const teacherData = response.data.map(teacher => ({
          label: teacher.name,
          value: teacher._id,
        }));
        setTeachers(teacherData);
      })
      .catch(error => {
        Notify.failure("Erro ao buscar os professores");
      });
  }, []); 

  const handleTeacherChange = selectedOption => {
    setSelectedTeacher(selectedOption);
  };

  console.log(teachers)

  const Container = styled.div`
    display: flex;
    flex-direction: column;
    background-color: #fff;
    border-radius: 10px;
    padding: 1rem;
    height: 100%;
    overflow-y: auto;
  `;

  const Select = styled(ReactAsync)`
    width: 50%;
    margin-bottom: 2rem;
    .css-13cymwt-control,
    .css-t3ipsp-control {
      border: 2px solid ${(props) => props.theme.colors.background};
      border-radius: 0.5rem;
    }
    .css-13cymwt-control:hover,
    .css-t3ipsp-control:hover {
      border-color: ${(props) => props.theme.colors.secundary};
      cursor: pointer;
    }
    .css-t3ipsp-control {
      box-shadow: none;
    }
  `;

  const styles = {
    wrap: {
      display: "flex",
    },
    left: {
      marginRight: "10px",
    },
    main: {
      flexGrow: "1",
    },
  };

  const handleChange = selectedOption => {
    setSelectedTeacher(selectedOption);
  };
  const filterColors = (inputValue) => {
		return teachers.filter((i) =>
			i.label.toLowerCase().includes(inputValue.toLowerCase())
		);
	};

	const loadOptions = (inputValue, callback) => {
		setTimeout(() => {
			callback(filterColors(inputValue));
		}, 500);
	};

  useEffect(() => {
    console.log(selectedTeacher)
    setTeacherParam(selectedTeacher ? (selectedTeacher.value || "all") : "all");
    console.log(teacherParam)
  }, [selectedTeacher]) 

  return (
    <>
      <Container>
        <div className="cHeader">
        {teachers.length > 0 && ( // Verifica se há dados nos professores
          <Select
            loadOptions={loadOptions}
            defaultOptions
            onChange={handleChange}
            placeholder="Escolha um formador"
          />
        )}
          <Add to={"/Admin/calendar/form"}>Adicionar</Add> {/* esse botão vai precisar de se comportar da mesma forma que o antigo criar evento do calendário*/}
        </div>

        <div className="calendario">
          <iframe
            src={"http://localhost:3000/?professorId=" + (teacherParam)}
            frameborder="0"
            height="700px"
            width="100%"

          ></iframe>
        </div>
      </Container>
    </>
  );
}
