import { createContext, useState } from "react";
import { api } from "../api/api";
import { login as backendLogin } from '../../src/api/nova';
import cryptoJs from "crypto-js";

export const AuthContext = createContext();

const AuthProvider = ({ children }) => {
	const [isLoading, setIsLoading] = useState(false);
	const [user, setUser] = useState({});
	const [notificationsAmount, setNotificationsAmount] = useState("");
	const isAuth = !!Object.keys(user).length;

	const setUserData = (userData) => {
		if (!userData) return;

		setUser({
			id: userData._id,
			username: userData.username,
			email: userData.email,
			role: userData.role,
		});

		//  api.get(`/messages/${userData._id}/unread_amount`).then((result) => {
		//    setNotificationsAmount(result.data);
		//  });
	};

	useState(() => {
		setUserData(JSON.parse(localStorage.getItem("user")));
	}, []);

	const login = async ({ userOrEmail, password }) => {
		setIsLoading(true);
	
		try {
		  const result = await backendLogin(userOrEmail, password); // Chama a nova função de login
		  if (result && result.login) {
			setUserData(result); // Ajuste conforme a estrutura dos dados retornados pela nova função
			localStorage.setItem("user", JSON.stringify(result)); // Ajuste conforme a estrutura dos dados retornados
		  }
		  
		  setIsLoading(false);
		  return result;
		} catch (error) {
		  setIsLoading(false);
		  return { login: false, message: error.message }; // Tratamento de erro caso ocorra na nova função de login
		}
	  };

	return (
		<AuthContext.Provider
			value={{
				login,
				isLoading,
				user,
				notificationsAmount,
				isAuth,
			}}
		>
			{children}
		</AuthContext.Provider>
	);
};

export default AuthProvider;
