import { useRef, useState } from "react";
import { useParams } from "react-router-dom";
import { useForm } from "react-hook-form";
import { api } from "../../../../api/api";
import { Notify } from "notiflix/build/notiflix-notify-aio";
import { useNavigate } from "react-router-dom";

const CreateLessonForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [selectedFormador, setSelectedFormador] = useState("");
  const [selectedModule, setSelectedModule] = useState("");
  const [selectedCourse, setSelectedCourse] = useState("");

  const methods = useForm();

  const onSubmit = (data) => {
    // Enviar dados para a API ou realizar ações necessárias com os dados do formulário
    console.log(data);
  };

  return (
    <>
      <form onSubmit={methods.handleSubmit(onSubmit)}>
        <div>
          <label>Título da aula:</label>
          <input {...methods.register("lessonTitle", { required: "Este campo é obrigatório" })} type="text" placeholder="Digite o título da aula" />
          {methods.errors.lessonTitle && (
            <span>{methods.errors.lessonTitle.message}</span>
          )}
        </div>
        <div>
          <label>Data de início:</label>
          <input {...methods.register("lessonStart", { required: "Este campo é obrigatório" })} type="date" />
          {methods.errors.lessonStart && (
            <span>{methods.errors.lessonStart.message}</span>
          )}
        </div>
        <div>
          <label>Data de término:</label>
          <input {...methods.register("lessonEnd", { required: "Este campo é obrigatório" })} type="date" />
          {methods.errors.lessonEnd && (
            <span>{methods.errors.lessonEnd.message}</span>
          )}
        </div>
        <div>
          <label>Formador:</label>
          <select value={selectedFormador} onChange={(e) => setSelectedFormador(e.target.value)}>
            {/* Opções para formadores */}
          </select>
        </div>
        <div>
          <label>Módulo:</label>
          <select value={selectedModule} onChange={(e) => setSelectedModule(e.target.value)}>
            {/* Opções para módulos */}
          </select>
        </div>
        <div>
          <label>Curso:</label>
          <select value={selectedCourse} onChange={(e) => setSelectedCourse(e.target.value)}>
            {/* Opções para cursos */}
          </select>
        </div>
        <div>
          <button type="submit">Criar Aula</button>
        </div>
      </form>
    </>
  );
};

export default CreateLessonForm;
