import styled from "styled-components";
import React, { useState, useRef, useEffect } from "react";
import ReactAsync from "react-select/async";
import "./CalendarStyles.css";
import {
	Container,
	ContainerHeader,
	SmallContainer,
	Header,
	TableIconContainer,
	TableRow,
	Search,
	Edit,
	Add,
	EditIcon,
	HeaderDivider,
	TableField,
} from "../../../styles/AdminStyles";
import axios from 'axios';

export default function Index() {

  const [teachers, setTeachers] = useState([]);
  const [selectedTeacher, setSelectedTeacher] = useState(null);

  useEffect(() => {
    // Faz a requisição para obter os professores
    axios.get("/teachers")
      .then(response => {
        // Obtém os nomes dos professores e cria opções para o Select
        const teacherOptions = response.data.map(teacher => ({
          value: teacher._id, // Aqui você pode usar o ID ou outro identificador único do professor
          label: teacher.name // Use o campo "name" ou o campo correto que contém os nomes dos professores
        }));
        setTeachers(teacherOptions);
      })
      .catch(error => {
        // Trate qualquer erro aqui
        console.error("Erro ao obter os professores:", error);
      });
  }, []);



  const loadOptions = (inputValue, callback) => {
    setTimeout(() => {
      const filteredOptions = options.filter(option =>
        option.label.toLowerCase().includes(inputValue.toLowerCase())
      );
      callback(filteredOptions);
    }, 1000); // Simulando um atraso de 1 segundo para a busca
  };

  const Container = styled.div`
    display: flex;
    flex-direction: column;
    background-color: #fff;
    border-radius: 10px;
    padding: 1rem;
    height: 100%;
    overflow-y: auto;
  `;

  const Select = styled(ReactAsync)`
    width: 50%;
    margin-bottom: 2rem;
    .css-13cymwt-control,
    .css-t3ipsp-control {
      border: 2px solid ${(props) => props.theme.colors.background};
      border-radius: 0.5rem;
    }
    .css-13cymwt-control:hover,
    .css-t3ipsp-control:hover {
      border-color: ${(props) => props.theme.colors.secundary};
      cursor: pointer;
    }
    .css-t3ipsp-control {
      box-shadow: none;
    }
  `;

  const styles = {
    wrap: {
      display: "flex",
    },
    left: {
      marginRight: "10px",
    },
    main: {
      flexGrow: "1",
    },
  };
  const [selectedOption, setSelectedOption] = useState(null);
  const handleChange = selectedOption => {
    setSelectedOption(selectedOption);
  };

  return (
    <>
      <Container>
        <div className="cHeader">
          <Select
             loadOptions={loadOptions}
             defaultOptions
             onChange={handleChange}
             value={selectedOption}
             placeholder="Escolha um formador"
          />
          <Add to={"/Admin/calendar/form"}>Adicionar</Add> {/* esse botão vai precisar de se comportar da mesma forma que o antigo criar evento do calendário*/}
        </div>

        <div className="calendario">
          <iframe
            src="http://localhost:3000/"
            frameborder="0"
            height="700px"
            width="100%"

          ></iframe>
        </div>
      </Container>
    </>
  );
}
